 $inputLabelColor: $greyscale900; // label color
 $inputPlaceHolderColor: $greyscale500; // 플레이스 홀더 컬러
 $inputBoxHeight: 54px; // 박스형 인풋 높이 (디자인 56px에서 상/하단 border 2px 뺀 값)

+// 인풋 기본 스타일 mixin
+@mixin input-default {
+	display: flex;
+	align-items: center;
+	transition: color 200ms ease-in-out, background-color 200ms ease-in-out, border-color 200ms ease-in-out, z-index 200ms step-end;
+	will-change: color, background-color, border-color;
+
+	.buttons {
+		flex: 0 0 auto;
+		line-height: 1;
+		transition: margin 100ms step-end;
+		will-change: margin;
+	}
+
+	input,
+	textarea,
+	.text-area-hidden {
+		@include text14;
+
+		width: 100%;
+		box-sizing: border-box;
+		padding: 0;
+		border: none;
+		background: none;
+		font-weight: 300;
+
+		&:disabled,
+		&:read-only {
+			color: $inputDisableColor;
+		}
+
+		&:read-only {
+			color: $inputReadonlyColor;
+		}
+
+		&[type="password"] {
+			font-family: Verdana, sans-serif !important;
+			font-size: 29px;
+
+			&::placeholder {
+				@include text14;
+
+				font-family: shinhan !important;
+			}
+
+			&:placeholder-shown {
+				@include text14;
+			}
+		}
+
+		&::placeholder {
+			color: $inputPlaceHolderColor;
+		}
+	}
+
+	textarea {
+		resize: none;
+	}
+
+	.text-area-hidden {
+		white-space: pre;
+	}
+
+	&:focus-within {
+		border-color: $inputFocusColor;
+		transition: color 200ms ease-in-out, background-color 200ms ease-in-out, border-color 200ms ease-in-out, z-index 200ms step-start;
+
+		.buttons {
+			transition: margin 100ms step-start;
+		}
+
+		.reset-button {
+			width: 24px;
+			opacity: 1;
+			transition: opacity 100ms ease-in-out, width 100ms step-start, margin 100ms step-start;
+		}
+	}
+
+	.reset-button {
+		width: 0;
+		height: 24px;
+		margin: 0;
+		background: url('../../styles/assets/images/icon/icon-textfield-reset.svg') no-repeat;
+		opacity: 0;
+		transition: opacity 100ms ease-in-out, width 100ms step-end, margin 100ms step-end;
+		will-change: opacity, width, margin;
+	}
+
+	.input-click-wrap {
+		display: flex;
+		align-items: center;
+
+		input,
+		textarea,
+		.text-area-hidden {
+			flex: 1;
+		}
+
+		&.align-right {
+			flex-direction: row-reverse;
+
+			input {
+				text-align: right;
+			}
+		}
+	}
+
+	&.input-nested-error {
+		border-color: $inputErrorColor;
+
+		& + .message {
+			color: $inputErrorColor;
+		}
+		@content;
+	}
+
+	&.input-disabled {
+		.unit {
+			color: $inputDisableColor;
+		}
+	}
+
+	&.input-readonly {
+		.unit {
+			color: $inputReadonlyColor;
+		}
+	}
+
+	@at-root {
+		label:not(:global(.hidden)) ~ &,
+		span.normal ~ & {
+			margin-top: 8px;
+		}
+
+		label:not(:global(.hidden)),
+		span.normal {
+			@include text14 {
+				color: $inputLabelColor;
+				font-weight: 500;
+			}
+		}
+	}
+
+	button[class*="arrow-"] {
+		width: 24px;
+		height: 24px;
+		flex: 0 0 24px;
+		background: url(~@styles/assets/images/icon/icon-arrow-right-24.svg) center center no-repeat;
+
+		&.arrow-down {
+			background: url(~@styles/assets/images/icon/icon-arrow-down-24.svg) center center no-repeat;
+			transition: transform 0.3s ease-in-out;
+
+			&.drawer-active {
+				transform: rotate(180deg);
+			}
+		}
+	}
+
+	&.drawer-active button[class*="arrow-"] {
+		transform: rotate(180deg);
+	}
+}
+

